SELECT 
    e.staff_id,
    e.first_name,
    e.last_name,
    COUNT(a.id) as attendance_count
FROM employees e
LEFT JOIN attendances a ON e.id = a.employee_id 
WHERE e.staff_id IN ('35', '1', '2') 
  AND e.is_active = 1
  AND a.attendance_date BETWEEN '2025-04-01' AND '2025-04-30'
