SELECT 
    e.id,
    e.staff_id,
    e.first_name,
    e.last_name,
    COUNT(a.id) as attendance_count
FROM employees e
LEFT JOIN attendances a ON e.id = a.employee_id 
WHERE a.attendance_date BETWEEN '2025-04-01' AND '2025-04-30'
GROUP BY e.id, e.staff_id, e.first_name, e.last_name
ORDER BY e.id;