-- Verify the inserted records
SELECT 
    e.staff_id,
    e.first_name,
    COUNT(a.id) as total_days,
    MIN(a.attendance_date) as first_date,
    MAX(a.attendance_date) as last_date
FROM attendances a
JOIN employees e ON a.employee_id = e.id
WHERE e.staff_id IN ('1', '2', '3') AND a.attendance_date BETWEEN '2025-08-12' AND '2025-09-10'
GROUP BY e.staff_id, e.first_name
ORDER BY e.staff_id;